CREATE TABLE `CareerArticles` (
  `id` int(14) NOT NULL auto_increment,
  `cat_id` tinyint(3) NOT NULL default '0',
  `title` varchar(255) default NULL,
  `file_name` varchar(255) default NULL,
  `author` varchar(255) default NULL,
  `short_desc` text,
  `body` text,
  PRIMARY KEY  (`id`)
) TYPE=MyISAM;

